export default function Introduction() {
  const buttonAboutMe = {
    border: '4px solid #C7C7C7',
    padding: '.8rem 2.5rem',
    fontSize: '1.1rem',
  };

  const styleSectionIntroducao = {
    backgroundColor: '#221824',
    color: 'white',
    height: '95vh',
    textAlign: 'center',
  };
  return (
    <section className="container center" style={styleSectionIntroducao}>
      <h1 style={{ color: '#cc005f', fontSize: '2rem', lineHeight: '50%' }}>
        Olá!
      </h1>
      <h2 style={{ fontSize: '5rem' }}>Eu sou Kauã</h2>
      <h3 style={{ fontSize: '1.5rem' }}>Front End Developer</h3>
      <a href="#" style={buttonAboutMe}>
        Sobre mim{' '}
        <span>
          <img src={arrow} alt="arrow down" />
        </span>
      </a>
    </section>
  );
}
